import React, { useState, useEffect } from 'react';

const ColorAnalysis = () => {
  const [analysisResults, setAnalysisResults] = useState([]);

  useEffect(() => {
    const intervalId = setInterval(() => {
      const storedResults = localStorage.getItem('analysisResults');
      if (storedResults) {
        setAnalysisResults(JSON.parse(storedResults));
      }
    }, 1000);

    // Clear interval on component unmount
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div style={{ marginLeft: '20px', fontFamily: 'Arial, sans-serif' }}>
    <h3 style={{ fontSize: '1.5rem', fontWeight: 'bold', marginBottom: '10px' }}>Analysis Results</h3>
    {analysisResults.length === 0 ? (
      <p>No analysis results found.</p>
    ) : (
      <div>
        <div style={{ marginBottom: '20px' }}>
          <h3 style={{ fontSize: '1.2rem', marginBottom: '5px' }}>Your Seasonal Color</h3>
          <p style={{ fontSize: '1rem', marginBottom: '10px', color: '#666' }}>
            Discover your ideal color palette.
          </p>
          <div style={{ display: 'flex', flexWrap: 'wrap' }}>
            {analysisResults[1].slice(1, -1).split(', ').map((color, index) => (
              <div key={index} style={{ marginRight: '10px', marginBottom: '10px' }}>
                <div
                  style={{
                    backgroundColor: color,
                    width: '130px',
                    height: '130px',
                    borderRadius: '50%',
                    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
                  }}
                ></div>
              </div>
            ))}
          </div>
        </div>
        <div>
          <h3 style={{ fontSize: '1.2rem', marginBottom: '5px' }}>Colors You Should Avoid</h3>
          <div style={{ display: 'flex', flexWrap: 'wrap' }}>
            {analysisResults[2].slice(1, -1).split(', ').map((color, index) => (
              <div key={index} style={{ marginRight: '10px', marginBottom: '10px' }}>
                <div
                  style={{
                    backgroundColor: color,
                    width: '130px',
                    height: '130px',
                    borderRadius: '50%',
                    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
                  }}
                ></div>
              </div>
            ))}
          </div>
        </div>
      </div>
    )}
  </div>


  );
};

export default ColorAnalysis;
