import React, { useState } from 'react';

const FaceRead = () => {
  const [imagePreview, setImagePreview] = useState(null);
  const [selectedColor, setSelectedColor] = useState(null);
  const [skinColor, setSkinColor] = useState(null);
  const [hairColor, setHairColor] = useState(null);
  const [eyeColor, setEyeColor] = useState(null);

  const handleImageUpload = () => {
    const fileInput = document.createElement('input');
    fileInput.setAttribute('type', 'file');
    fileInput.setAttribute('accept', 'image/*');

    fileInput.addEventListener('change', (event) => {
      const file = event.target.files[0];
      const reader = new FileReader();

      reader.onload = function(event) {
        setImagePreview(event.target.result);
      }

      if (file) {
        reader.readAsDataURL(file);
      }
    });

    fileInput.click();
  };

  const handleColorSelection = (color) => {
    setSelectedColor(color);
  };

  const handleImageClick = (event) => {
    if (!selectedColor) return;

    const canvas = document.createElement('canvas');
    canvas.width = event.target.width;
    canvas.height = event.target.height;
    const ctx = canvas.getContext('2d');
    ctx.drawImage(event.target, 0, 0, canvas.width, canvas.height);

    const pixelData = ctx.getImageData(event.nativeEvent.offsetX, event.nativeEvent.offsetY, 1, 1).data;
    const hexColor = rgbToHex(pixelData[0], pixelData[1], pixelData[2]);

    switch (selectedColor) {
      case 'skin':
        setSkinColor(hexColor);
        break;
      case 'hair':
        setHairColor(hexColor);
        break;
      case 'eye':
        setEyeColor(hexColor);
        break;
      default:
        break;
    }

    setSelectedColor(null);
  };

  const rgbToHex = (r, g, b) => {
    return "#" + ((1 << 24) + (r << 16) + (g << 8) + b).toString(16).slice(1);
  };

  return (
    <div>
      <div className="routes-title text-left">Update Photo and Choose Colors</div>
      <button onClick={handleImageUpload}>Upload Image</button>
      {imagePreview && (
        <div>
          <h3>Preview:</h3>
          <img
            src={imagePreview}
            alt="Uploaded"
            style={{ maxWidth: '100%', maxHeight: '300px', cursor: selectedColor ? 'crosshair' : 'auto' }}
            onClick={handleImageClick}
          />
          <div style={{ marginTop: '10px' }}>
            <button className="color-button" style={{ backgroundColor: skinColor }} onClick={() => handleColorSelection('skin')}>Skin Color</button>
            <span style={{ marginLeft: '10px' }}>{skinColor}</span>
          </div>
          <div style={{ marginTop: '10px' }}>
            <button className="color-button" style={{ backgroundColor: hairColor }} onClick={() => handleColorSelection('hair')}>Hair Color</button>
            <span style={{ marginLeft: '10px' }}>{hairColor}</span>
          </div>
          <div style={{ marginTop: '10px' }}>
            <button className="color-button" style={{ backgroundColor: eyeColor }} onClick={() => handleColorSelection('eye')}>Eye Color</button>
            <span style={{ marginLeft: '10px' }}>{eyeColor}</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default FaceRead;
