import React, { useState, useEffect } from 'react';

const ColorAnalysis = () => {
  const [analysisResults, setAnalysisResults] = useState([]);

  useEffect(() => {
    const intervalId = setInterval(() => {
      const storedResults = localStorage.getItem('analysisResults');
      if (storedResults) {
        setAnalysisResults(JSON.parse(storedResults));
      }
    }, 1000);

    // Clear interval on component unmount
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div style={{ marginLeft: '20px' }}>
    <h3>Analysis Results:</h3>
    {analysisResults.length === 0 ? (
      <p>No analysis results found.</p>
    ) : (
      <div>
        <h3>Your Seasonal Color Palette:</h3>
        <p>Find your perfect palette and elevate your style.</p>
        <div style={{ marginBottom: '20px' }}>
          <h4>Seasonal Color:</h4>
          <p>{analysisResults[0]}</p>
        </div>
        <div style={{ marginBottom: '20px' }}>
          <h4>Colors You Should Wear:</h4>
          <div style={{ display: 'flex', flexWrap: 'wrap' }}>
            {analysisResults[1].split(', ').map((color, index) => (
              <div key={index} style={{ marginRight: '10px', marginBottom: '10px', textAlign: 'center' }}>
                <div
                  style={{
                    backgroundColor: color,
                    width: '100px',
                    height: '100px',
                    borderRadius: '50%',
                    border: '2px solid #000',
                    margin: '0 auto', // Center the color circle
                  }}
                ></div>
                <p>{color}</p>
              </div>
            ))}
          </div>
        </div>
        <div>
          <h4>Colors You Should Avoid:</h4>
          <div style={{ display: 'flex', flexWrap: 'wrap' }}>
            {analysisResults[2].split(', ').map((color, index) => (
              <div key={index} style={{ marginRight: '10px', marginBottom: '10px', textAlign: 'center' }}>
                <div
                  style={{
                    backgroundColor: color,
                    width: '100px',
                    height: '100px',
                    borderRadius: '50%',
                    border: '2px solid #000',
                    margin: '0 auto', // Center the color circle
                  }}
                ></div>
                <p>{color}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    )}
  </div>



  );
};

export default ColorAnalysis;
