import React, { useState, useEffect } from 'react';

const ColorAnalysis = () => {
  const [analysisResults, setAnalysisResults] = useState([]);

  useEffect(() => {
    const intervalId = setInterval(() => {
      const storedResults = localStorage.getItem('analysisResults');
      if (storedResults) {
        setAnalysisResults(JSON.parse(storedResults));
      }
    }, 1000);

    // Clear interval on component unmount
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div style={{ marginLeft: '20px' }}>
  <h3>Your seasonal color is: {analysisResults[0]}</h3>
  <p> For more information about what this means, check out[insert link]</p>
  <h3> Colors you would look good in:</h3>
  <div>

    {analysisResults[1].split(', ').map((color, index) => (
      <button
        key={index}
        style={{ backgroundColor: color, width: '100px', height: '100px', margin: '5px', border: 'none', cursor: 'default' }}
        disabled
      >
        {color}
      </button>
    ))}
  </div>
  <h3> Colors you should avoid</h3>
  <div>
    {analysisResults[2].split(', ').map((color, index) => (
      <button
        key={index}
        style={{ backgroundColor: color, width: '100px', height: '100px', margin: '5px', border: 'none', cursor: 'default' }}
        disabled
      >
        {color}
      </button>
    ))}
  </div>
</div>

  );
};

export default ColorAnalysis;
